# Function to check if the CEO's first or last name matches the Indian names list
def is_indian_ceo(ceo_name):
    if pd.isna(ceo_name) or not ceo_name.strip():  # Handle NaN or empty values
        return "No"
    
    ceo_parts = ceo_name.split()  # Split the CEO name into parts (first name, last name, etc.)
    
    # Check first name
    first_name = ceo_parts[0]
    if first_name.lower() in (name.lower() for name in indian_names):
        return "Yes"
    
    # Check last name (if it exists)
    if len(ceo_parts) > 1:  # Ensure there's a last name
        last_name = ceo_parts[-1]
        if last_name.lower() in (name.lower() for name in indian_names):
            return "Yes"
    
    return "No"

# Read the Excel file
input_file = "archive/nasdaq_screener.csv"  # Replace with your actual file name
output_file = "archive/nasdaq_screener_with_indian_ceos.csv"

print(f"Reading data from {input_file}...")
df = pd.read_csv(input_file, sep=";")  # Skip problematic rows

# Check if the CEO is Indian and add a new column
print("Checking if CEOs are Indian...")
df["Indian"] = df["CEO"].apply(is_indian_ceo)

# Save the updated DataFrame to a new CSV file
print(f"Writing updated data to {output_file}...")
df.to_csv(output_file, index=False)

print("Process completed successfully!")